<?phprequire_once '../../config/db_connect.php';$kelas=$_GET['kelas'];$tapel=$_GET['tapel'];$smt=$_GET['smt'];$ab=substr($kelas,0,1);$output = array('data' => array());$sql = "select * from penempatan where rombel='$kelas' and tapel='$tapel' and smt='$smt' order by nama asc";$query = $connect->query($sql);while($s=$query->fetch_assoc()) {	$idp=$s['peserta_didik_id'];	$sql1 = "select * from siswa where peserta_didik_id='$idp'";	$query1 = $connect->query($sql1);	$row1 = $query1->fetch_assoc();	$sqlp = "SELECT * FROM data_ekskul WHERE peserta_didik_id='$idp' and smt='$smt' and tapel='$tapel' order by id_ekskul asc";	$cek = $connect->query($sqlp)->num_rows;	$tbl='	<a class="btn btn-primary btn-xs" data-bs-toggle="modal" data-bs-target="#modalekskul" data-kelas="'.$kelas.'" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idp.'" id="getKes"><i class="fa fa-plus-circle"></i></a>	';	if($cek>0){		$queryt = $connect->query($sqlp);		$actionButton = '';		while($pn=$queryt->fetch_assoc()) {			$dte=$pn['id'];			$nm=$pn['id_ekskul'];			$nme = $connect->query("SELECT * FROM ekskul WHERE id_ekskul='$nm'")->fetch_assoc();			$halo=$nme['nama_ekskul'];			$actionButton = $actionButton.'			<div class="alert alert-success alert-dismissible" role="alert">							<h3 class="alert-heading fs-5 fw-bold mb-1">'.$halo.'</h3>							<p class="mb-0">							  '.$pn['keterangan'].'							  <button type="button" class="btn btn-outline-danger btn-sm me-1 mb-1" onclick="removeEkskul('.$dte.')">								  <i class="fa fa-times opacity-50 me-1"></i>Hapus							  </button>							</p>						</div>			';		};			}else{		$actionButton = '';	};	$output['data'][] = array(		$tbl,		$row1['nama'],		$actionButton	);	};// database connection close$connect->close();echo json_encode($output);