<?phprequire_once '../../config/db_connect.php';$kelas=$_GET['kelas'];$tapel=$_GET['tapel'];$smt=$_GET['smt'];$ab=substr($kelas,0,1);$output = array('data' => array());$sql = "select * from penempatan where rombel='$kelas' and tapel='$tapel' and smt='$smt' order by nama asc";$query = $connect->query($sql);while($s=$query->fetch_assoc()) {	$idp=$s['peserta_didik_id'];	$sql1 = "select * from siswa where peserta_didik_id='$idp'";	$query1 = $connect->query($sql1);	$row1 = $query1->fetch_assoc();	$sqlp = "SELECT * FROM data_kesehatan WHERE peserta_didik_id='$idp' and smt='$smt' and tapel='$tapel'";	$pn = $connect->query($sqlp)->fetch_assoc();	$ids=$pn['id'];	$tng=$pn['tinggi'];	$brt=$pn['berat'];	$telinga=$pn['pendengaran'];	$mata=$pn['penglihatan'];	$gg=$pn['gigi'];	$lain=$pn['lainnya'];	$actionButton = '	<button class="btn btn-effect-ripple btn-xs btn-primary" data-bs-toggle="modal" data-bs-target="#modalkesehatan" data-kelas="'.$kelas.'" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idp.'" id="getKes"><i class="fa fa-plus-circle"></i></button>	';	$output['data'][] = array(		$actionButton,		$row1['nama'],		$tng,		$brt,		$telinga,		$mata,		$gg,		$lain	);	};// database connection close$connect->close();echo json_encode($output);