<?php require_once '../../config/db_connect.php';//if form is submittedif($_POST) {		$validator = array('success' => false, 'messages' => array());	$tapel=$_POST['ptapel'];	$smt=$_POST['psmt'];	$status=$_POST['pstatus'];	$sql = "UPDATE konfigurasi SET tapel='$tapel', semester='$smt', maintenis='$status' WHERE id_conf='1'";		$query = $connect->query($sql);
	if($query === TRUE) {						$validator['success'] = true;			$validator['messages'] = "Status Server berhasil diubah!";				} else {					$validator['success'] = false;			$validator['messages'] = "Error while adding the member information";		};	// close the database connection	$connect->close();	echo json_encode($validator);}